package com.management.oa.pojo;public class Organization{    private Integer id;    private String name;    private String icon;    private String flag;    private Integer groupId;    private Integer orderBy;    private Integer fooId;    public Organization(Integer id, String name, String icon, String flag, Integer groupId, Integer orderBy, Integer fooId)    {        this.id = id;        this.name = name;        this.icon = icon;        this.flag = flag;        this.groupId = groupId;        this.orderBy = orderBy;        this.fooId = fooId;    }    public Organization() {}    public Integer getId()    {        return this.id;    }    public void setId(Integer id)    {        this.id = id;    }    public String getName()    {        return this.name;    }    public void setName(String name)    {        this.name = (name == null ? null : name.trim());    }    public String getIcon()    {        return this.icon;    }    public void setIcon(String icon)    {        this.icon = (icon == null ? null : icon.trim());    }    public String getFlag()    {        return this.flag;    }    public void setFlag(String flag)    {        this.flag = (flag == null ? null : flag.trim());    }    public Integer getGroupId()    {        return this.groupId;    }    public void setGroupId(Integer groupId)    {        this.groupId = groupId;    }    public Integer getOrderBy()    {        return this.orderBy;    }    public void setOrderBy(Integer orderBy)    {        this.orderBy = orderBy;    }    public Integer getFooId()    {        return this.fooId;    }    public void setFooId(Integer fooId)    {        this.fooId = fooId;    }}